USE SCHEMA PETS_DB.DW;

TRUNCATE TABLE STG_SEATTLE_PET_LICENSE;

COPY INTO STG_SEATTLE_PET_LICENSE
FROM @azure_parquet_stage/seattle_pet_licenses.parquet
FILE_FORMAT = (TYPE = PARQUET)
MATCH_BY_COLUMN_NAME = CASE_INSENSITIVE
ON_ERROR = 'CONTINUE';

SELECT COUNT(*) AS rows_loaded FROM STG_SEATTLE_PET_LICENSE;

USE SCHEMA PETS_DB.DW;

TRUNCATE TABLE STG_LOCATION_ZIP_CITY;

COPY INTO STG_LOCATION_ZIP_CITY
FROM @azure_parquet_stage/geo_data.parquet
FILE_FORMAT = (TYPE = PARQUET)
MATCH_BY_COLUMN_NAME = CASE_INSENSITIVE
ON_ERROR = 'CONTINUE';

SELECT COUNT(*) AS rows_loaded FROM STG_LOCATION_ZIP_CITY;
